import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function UserRegister() {
  const [user, setUser] = useState({ username: "", password: "" });
  const navigate = useNavigate();

  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:8080/user/register", user);
      alert(response.data);
      navigate("/user-login");
    } catch (error) {
      console.error("Registration failed:", error);
      alert("Registration failed!");
    }
  };

  return (
    <div className="p-4 bg-gray-900 text-white rounded-md">
      <h2 className="text-xl font-bold">User Registration</h2>
      <form onSubmit={handleSubmit} className="flex flex-col gap-2">
        <input type="text" name="username" placeholder="Username" onChange={handleChange} className="p-2 text-black"/>
        <input type="password" name="password" placeholder="Password" onChange={handleChange} className="p-2 text-black"/>
        <button type="submit" className="p-2 bg-blue-600 rounded-md">Register</button>
      </form>
      <p>Already have an account? <a href="/user-login" className="text-blue-400">Login</a></p>
    </div>
  );
}
