import React, { useState } from "react";
import axios from "axios";

export default function PersonalizedNews() {
  const [selectedCategory, setSelectedCategory] = useState(""); // Selected category
  const [newsList, setNewsList] = useState([]); // Stores fetched news
  const [loading, setLoading] = useState(false); // Loading state
  const [error, setError] = useState(""); // Error handling

  // Predefined list of categories (exactly as stored in the database)
  const categories = [
    "SPORTS", "ENTERTAINMENT", "POLITICS", "TECHNOLOGY",
    "HEALTH", "BUSINESS", "SCIENCE", "EDUCATION"
  ];

  // Fetch personalized news based on the selected category
  const fetchPersonalizedNews = async (e) => {
    e.preventDefault();
    if (!selectedCategory) {
      alert("Please select a category.");
      return;
    }

    setLoading(true);
    setError("");
    setNewsList([]);

    try {
      // Convert user input to uppercase to match SQL storage
      const formattedCategory = categories.find(
        (cat) => cat.toLowerCase() === selectedCategory.toLowerCase()
      );

      if (!formattedCategory) {
        setError("Invalid category selected.");
        setLoading(false);
        return;
      }

      // âœ… Send 'prefDetails' instead of 'category'
      const response = await axios.get("http://localhost:8080/admin/personalizedNews", {
        params: { prefDetails: formattedCategory }, // FIXED API CALL
      });

      if (response.data.length === 0) {
        setError("No news found for this category.");
      } else {
        setNewsList(response.data);
      }
    } catch (error) {
      console.error("Error fetching personalized news:", error);
      setError("Failed to fetch personalized news. Please try again later.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="mb-6 p-6 bg-gray-800 text-white rounded-lg shadow-md">
      <form onSubmit={fetchPersonalizedNews} className="mb-4">
        <label className="block text-sm font-semibold mb-2">Select News Category:</label>
        <div className="flex">
          <select
            className="flex-1 px-4 py-2 rounded-md border border-gray-500 bg-gray-700 text-white focus:outline-none focus:border-blue-500"
            value={selectedCategory}
            onChange={(e) => setSelectedCategory(e.target.value)}
            required
          >
            <option value="">-- Select a Category --</option>
            {categories.map((category, index) => (
              <option key={index} value={category}>
                {category.charAt(0).toUpperCase() + category.slice(1).toLowerCase()} {/* Display in Title Case */}
              </option>
            ))}
          </select>
          <button
            type="submit"
            className="ml-2 px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 focus:outline-none"
          >
            {loading ? "Loading..." : "Fetch News"}
          </button>
        </div>
      </form>

      {/* Show error message if any */}
      {error && <p className="text-red-400">{error}</p>}

      {/* Show news list with 5 columns */}
      {newsList.length > 0 && !error && (
        <div>
          <h2 className="text-lg font-bold mb-2">Personalized News:</h2>
          <table className="w-full border-collapse border border-gray-500">
            <thead>
              <tr className="bg-gray-700">
                <th className="border border-gray-500 px-4 py-2">Title</th>
                <th className="border border-gray-500 px-4 py-2">Category</th>
                <th className="border border-gray-500 px-4 py-2">Author</th>
                <th className="border border-gray-500 px-4 py-2">Date</th>
                <th className="border border-gray-500 px-4 py-2">Content</th>
              </tr>
            </thead>
            <tbody>
              {newsList.map((news, index) => (
                <tr key={index} className="bg-gray-800 text-gray-300">
                  <td className="border border-gray-500 px-4 py-2">{news.title}</td>
                  <td className="border border-gray-500 px-4 py-2">{news.category}</td>
                  <td className="border border-gray-500 px-4 py-2">{news.author}</td>
                  <td className="border border-gray-500 px-4 py-2">{news.date}</td>
                  <td className="border border-gray-500 px-4 py-2">{news.content}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}
