import React, { useState } from "react";
import axios from "axios";

export default function UpdateNewsNews() {
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [category, setCategory] = useState("");
  const [author, setAuthor] = useState("");
  const url = "http://localhost:8080/admin";

  const addnews = async (e) => {
    e.preventDefault();

    const news = { title, content, category, author }; // Directly using an object

    try {
      const response = await axios.post(url + "/addNews", news);
      console.log("Data passed successfully:", response.data);
      alert("News added successfully!");
      setTitle("");
      setContent("");
      setCategory("");
      setAuthor("");
    } catch (error) {
      console.error("Error adding news:", error);
      alert("Failed to add news.");
    }
  };

  return (
    <div>
      <form onSubmit={addnews}>
        <div className="mb-3">
          <label htmlFor="title" className="form-label">
            Title:
          </label>
          <input
            type="text"
            id="title"
            className="form-control"
            required
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
        </div>

        <div className="mb-3">
          <label htmlFor="content" className="form-label">
            Content:
          </label>
          <textarea
            id="content"
            className="form-control"
            rows="4"
            required
            value={content}
            onChange={(e) => setContent(e.target.value)}
          />
        </div>

        <div className="mb-3">
          <label htmlFor="category" className="form-label">
            Category:
          </label>
          <input
            type="text"
            id="category"
            className="form-control"
            required
            value={category}
            onChange={(e) => setCategory(e.target.value)}
          />
        </div>

        <div className="mb-3">
          <label htmlFor="author" className="form-label">
            Author:
          </label>
          <input
            type="text"
            id="author"
            className="form-control"
            required
            value={author}
            onChange={(e) => setAuthor(e.target.value)}
          />
        </div>

        <button type="submit" className="btn btn-primary w-100">
          Add News
        </button>
      </form>
    </div>
  );
}
