import React, { useState } from "react";
import axios from "axios";

export default function PersonalizedNews() {
  const [selectedCategory, setSelectedCategory] = useState(""); // Selected category
  const [newsList, setNewsList] = useState([]); // Stores fetched news
  const [loading, setLoading] = useState(false); // Loading state
  const [error, setError] = useState(""); // Error handling

  // Predefined list of categories
  const categories = ["Sports", "Entertainment", "Politics", "Technology", "Health", "Business", "Science", "Education"];

  // Fetch personalized news based on the selected category
  const fetchPersonalizedNews = async (e) => {
    e.preventDefault();
    if (!selectedCategory) {
      alert("Please select a category.");
      return;
    }

    setLoading(true);
    setError("");

    try {
      const response = await axios.get("http://localhost:8080/admin/personalizedNews", {
        params: { category: selectedCategory },
      });

      if (response.data.length === 0) {
        setError("No news found for this category.");
      } else {
        setNewsList(response.data);
      }
    } catch (error) {
      console.error("Error fetching personalized news:", error);
      setError("Failed to fetch personalized news. Please try again later.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="mb-6 p-6 bg-gray-800 text-white rounded-lg shadow-md">
      <form onSubmit={fetchPersonalizedNews} className="mb-4">
        <label className="block text-sm font-semibold mb-2">Select News Category:</label>
        <div className="flex">
          <select
            className="flex-1 px-4 py-2 rounded-md border border-gray-500 bg-gray-700 text-white focus:outline-none focus:border-blue-500"
            value={selectedCategory}
            onChange={(e) => setSelectedCategory(e.target.value)}
            required
          >
            <option value="">-- Select a Category --</option>
            {categories.map((category, index) => (
              <option key={index} value={category}>
                {category}
              </option>
            ))}
          </select>
          <button
            type="submit"
            className="ml-2 px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 focus:outline-none"
          >
            {loading ? "Loading..." : "Fetch News"}
          </button>
        </div>
      </form>

      {/* Show error message if any */}
      {error && <p className="text-red-400">{error}</p>}

      {/* Show news list */}
      {newsList.length > 0 && !error && (
        <div>
          <h2 className="text-lg font-bold mb-2">Personalized News:</h2>
          <ul className="list-disc pl-5">
            {newsList.map((news, index) => (
              <li key={index} className="text-gray-300">
                {news.title} - <span className="italic">{news.category}</span>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
