import React, { useState } from "react";
import axios from "axios";

export default function PersonalizedNews({ setNewsList }) {
  const [category, setCategory] = useState("");
  const url = "http://localhost:8080/admin";

  const fetchPersonalizedNews = async () => {
    if (!category) {
      alert("Please enter a category.");
      return;
    }

    try {
      const response = await axios.get(`${url}/personalizedNews`, {
        params: { prefDetails: category },
      });

      setNewsList(response.data);
    } catch (error) {
      console.error("Error fetching personalized news:", error);
      alert("Failed to fetch personalized news.");
    }
  };

  return (
    <div className="mb-6">
      <label className="block text-sm font-semibold mb-2">Get News by Category:</label>
      <div className="flex">
        <input
          type="text"
          className="flex-1 px-4 py-2 rounded-md border border-gray-500 bg-gray-800 text-white focus:outline-none focus:border-blue-500"
          placeholder="Enter category"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
        />
        <button
          onClick={fetchPersonalizedNews}
          className="ml-2 px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 focus:outline-none"
        >
          Search
        </button>
      </div>
    </div>
  );
}
