import React, { useState } from "react";
import axios from "axios";

export default function DeleteNews({ onNewsDeleted }) {
  const [newsIdToDelete, setNewsIdToDelete] = useState("");
  const url = "http://localhost:8080/admin";

  const deleteNews = async (e) => {
    e.preventDefault();
    if (!newsIdToDelete) {
      alert("Please enter a valid News ID.");
      return;
    }

    try {
      const response = await axios.delete(`${url}/deleteNews/${newsIdToDelete}`);

      alert(response.data);
      setNewsIdToDelete("");
      onNewsDeleted(); // Refresh the news list
    } catch (error) {
      console.error("Error deleting news:", error);
      alert("Failed to delete news.");
    }
  };

  return (
    <form onSubmit={deleteNews} className="mb-6">
      <label className="block text-sm font-semibold mb-2">Delete News by ID:</label>
      <div className="flex">
        <input
          type="text"
          className="flex-1 px-4 py-2 rounded-md border border-gray-500 bg-gray-800 text-white focus:outline-none focus:border-red-500"
          placeholder="Enter News ID"
          value={newsIdToDelete}
          onChange={(e) => setNewsIdToDelete(e.target.value)}
          required
        />
        <button
          type="submit"
          className="ml-2 px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700 focus:outline-none"
        >
          Delete
        </button>
      </div>
    </form>
  );
}
