import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function UserLogin() {
  const [credentials, setCredentials] = useState({ username: "", password: "" });
  const navigate = useNavigate();

  const handleChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:8080/user/login", credentials);
      if (response.data) {
        alert("Login successful!");
        navigate("/user-dashboard");
      } else {
        alert("Invalid credentials");
      }
    } catch (error) {
      console.error("Login failed:", error);
      alert("Login failed!");
    }
  };

  return (
    <div className="p-4 bg-gray-900 text-white rounded-md">
      <h2 className="text-xl font-bold">User Login</h2>
      <form onSubmit={handleSubmit} className="flex flex-col gap-2">
        <input type="text" name="username" placeholder="Username" onChange={handleChange} className="p-2 text-black"/>
        <input type="password" name="password" placeholder="Password" onChange={handleChange} className="p-2 text-black"/>
        <button type="submit" className="p-2 bg-green-600 rounded-md">Login</button>
      </form>
      <p>Don't have an account? <a href="/user-register" className="text-blue-400">Register</a></p>
    </div>
  );
}
