import React, { useState } from "react";
import axios from "axios";

export default function UpdateNews() {
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [category, setCategory] = useState("");
  const [author, setAuthor] = useState("");
  const url = process.env.REACT_APP_API_URL || "http://localhost:8080/admin"; // Use env variable

  const addNews = async (e) => {
    e.preventDefault();
    const news = { title, content, category, author };

    try {
      const response = await axios.post(`${url}/addNews`, news);
      console.log("Data passed successfully:", response.data);
      alert("News added successfully!");
      setTitle("");
      setContent("");
      setCategory("");
      setAuthor("");
    } catch (error) {
      console.error("Error adding news:", error);
      alert("Failed to add news.");
    }
  };

  return (
    <div className="max-w-lg mx-auto p-6 bg-gray-900 text-white rounded-md shadow-md">
      <h2 className="text-lg font-semibold mb-4">Update News</h2>
      <form onSubmit={addNews}>
        <div className="mb-4">
          <label className="block text-sm font-semibold mb-2">Title:</label>
          <input
            type="text"
            className="w-full px-4 py-2 rounded-md border border-gray-500 bg-gray-800 text-white focus:outline-none focus:border-blue-500"
            required
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
        </div>

        <div className="mb-4">
          <label className="block text-sm font-semibold mb-2">Content:</label>
          <textarea
            className="w-full px-4 py-2 rounded-md border border-gray-500 bg-gray-800 text-white focus:outline-none focus:border-blue-500"
            rows="4"
            required
            value={content}
            onChange={(e) => setContent(e.target.value)}
          />
        </div>

        <div className="mb-4">
          <label className="block text-sm font-semibold mb-2">Category:</label>
          <input
            type="text"
            className="w-full px-4 py-2 rounded-md border border-gray-500 bg-gray-800 text-white focus:outline-none focus:border-blue-500"
            required
            value={category}
            onChange={(e) => setCategory(e.target.value)}
          />
        </div>

        <div className="mb-6">
          <label className="block text-sm font-semibold mb-2">Author:</label>
          <input
            type="text"
            className="w-full px-4 py-2 rounded-md border border-gray-500 bg-gray-800 text-white focus:outline-none focus:border-blue-500"
            required
            value={author}
            onChange={(e) => setAuthor(e.target.value)}
          />
        </div>

        <button
          type="submit"
          className="w-full py-2 px-4 bg-green-600 text-white rounded-md hover:bg-green-700 focus:outline-none"
        >
          Add News
        </button>
      </form>
    </div>
  );
}
