import React, { useState } from "react";
import axios from "axios";

export default function UpdateNewsById() {
  const [newsId, setNewsId] = useState("");
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [category, setCategory] = useState("");
  const [author, setAuthor] = useState("");
  const [loading, setLoading] = useState(false);
  const [news, setNews] = useState(null);
  const url = "http://localhost:8080/admin";

  // Fetch news by ID
  const fetchNews = async () => {
    if (!newsId) {
      alert("Please enter a valid News ID.");
      return;
    }

    setLoading(true);
    try {
      const response = await axios.get(`${url}/getNews/${newsId}`);
      const data = response.data;
      setNews(data);
      setTitle(data.title);
      setContent(data.content);
      setCategory(data.category);
      setAuthor(data.author);
    } catch (error) {
      console.error("Error fetching news:", error);
      alert("News not found with the given ID.");
      setNews(null);
    }
    setLoading(false);
  };

  // Update news details
  const handleUpdate = async (e) => {
    e.preventDefault();
    if (!news) {
      alert("Please fetch the news first.");
      return;
    }

    const updatedNews = { title, content, category, author };

    try {
      await axios.put(`${url}/updateNews/${newsId}`, updatedNews);
      alert("News updated successfully!");
    } catch (error) {
      console.error("Error updating news:", error);
      alert("Failed to update news.");
    }
  };

  return (
    <div className="p-6 bg-gray-900 text-white rounded-md shadow-md">
      <h2 className="text-lg font-semibold mb-4">Update News by ID</h2>

      {/* Input for entering News ID */}
      <div className="mb-4">
        <label className="block text-sm font-semibold mb-2">Enter News ID:</label>
        <input
          type="text"
          className="w-full px-4 py-2 rounded-md border border-gray-500 bg-gray-800 text-white focus:outline-none focus:border-blue-500"
          value={newsId}
          onChange={(e) => setNewsId(e.target.value)}
          required
        />
        <button
          className="mt-2 py-2 px-4 bg-blue-600 text-white rounded-md hover:bg-blue-700 focus:outline-none"
          onClick={fetchNews}
          disabled={loading}
        >
          {loading ? "Fetching..." : "Fetch News"}
        </button>
      </div>

      {/* Form to edit news details */}
      {news && (
        <form onSubmit={handleUpdate}>
          <div className="mb-4">
            <label className="block text-sm font-semibold mb-2">New Title:</label>
            <input
              type="text"
              className="w-full px-4 py-2 rounded-md border border-gray-500 bg-gray-800 text-white focus:outline-none focus:border-blue-500"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              required
            />
          </div>

          <div className="mb-4">
            <label className="block text-sm font-semibold mb-2">New Content:</label>
            <textarea
              className="w-full px-4 py-2 rounded-md border border-gray-500 bg-gray-800 text-white focus:outline-none focus:border-blue-500"
              rows="4"
              value={content}
              onChange={(e) => setContent(e.target.value)}
              required
            />
          </div>

          <div className="mb-4">
            <label className="block text-sm font-semibold mb-2">New Category:</label>
            <input
              type="text"
              className="w-full px-4 py-2 rounded-md border border-gray-500 bg-gray-800 text-white focus:outline-none focus:border-blue-500"
              value={category}
              onChange={(e) => setCategory(e.target.value)}
              required
            />
          </div>

          <div className="mb-6">
            <label className="block text-sm font-semibold mb-2">New Author:</label>
            <input
              type="text"
              className="w-full px-4 py-2 rounded-md border border-gray-500 bg-gray-800 text-white focus:outline-none focus:border-blue-500"
              value={author}
              onChange={(e) => setAuthor(e.target.value)}
              required
            />
          </div>

          <button type="submit" className="w-full py-2 px-4 bg-green-600 text-white rounded-md hover:bg-green-700 focus:outline-none">
            Update News
          </button>
        </form>
      )}
    </div>
  );
}
