import React, { useState } from "react";
import axios from "axios";

export default function AllNews() {
  const [newsList, setNewsList] = useState([]);
  const [loading, setLoading] = useState(false);
  const url = "http://localhost:8080/admin"; // Backend base URL

  // Fetch all news when button is clicked
  const fetchAllNews = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`${url}/allNews`);
      setNewsList(response.data);
    } catch (error) {
      console.error("Error fetching news:", error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-6 bg-gray-100 min-h-screen flex flex-col items-center">
      <div className="w-full max-w-4xl bg-white p-6 rounded-lg shadow-lg">
        <h3 className="text-2xl font-bold text-green-700 text-center mb-4">All News</h3>

        {/* Show All News Button */}
        <button
          onClick={fetchAllNews}
          className="mb-4 px-5 py-2 bg-green-600 text-white rounded-md hover:bg-green-700 transition duration-200"
          disabled={loading}
        >
          {loading ? "Loading..." : "Show All News"}
        </button>

        {/* News Table */}
        <div className="overflow-x-auto">
          <table className="w-full border-collapse border border-gray-300 rounded-lg text-left">
            <thead className="bg-green-600 text-white">
              <tr>
                <th className="border border-gray-300 px-4 py-3">ID</th>
                <th className="border border-gray-300 px-4 py-3">Title</th>
                <th className="border border-gray-300 px-4 py-3">Category</th>
                <th className="border border-gray-300 px-4 py-3">Author</th>
              </tr>
            </thead>
            <tbody>
              {newsList.length > 0 ? (
                newsList.map((news) => (
                  <tr key={news.id} className="bg-white text-gray-800 hover:bg-gray-100">
                    <td className="border border-gray-300 px-4 py-3">{news.id}</td>
                    <td className="border border-gray-300 px-4 py-3">{news.title}</td>
                    <td className="border border-gray-300 px-4 py-3">{news.category}</td>
                    <td className="border border-gray-300 px-4 py-3">{news.author || "N/A"}</td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="4" className="text-center p-4 text-gray-500">
                    No news found.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}
