import React, { useState, useEffect } from "react";
import axios from "axios";

export default function UpdateNews({ news, refreshNews, cancelEdit }) {
  // Ensure news exists before accessing properties
  const [title, setTitle] = useState(news?.title || "");
  const [content, setContent] = useState(news?.content || "");
  const [category, setCategory] = useState(news?.category || "");
  const [author, setAuthor] = useState(news?.author || "");

  useEffect(() => {
    if (news) {
      setTitle(news.title);
      setContent(news.content);
      setCategory(news.category);
      setAuthor(news.author);
    }
  }, [news]); // Update state when news changes

  const url = "http://localhost:8080/admin";

  const handleUpdate = async (e) => {
    e.preventDefault();
    if (!news || !news.id) {
      console.error("News ID is undefined, cannot update.");
      return;
    }

    const updatedNews = { title, content, category, author };

    try {
      await axios.put(`${url}/updateNews/${news.id}`, updatedNews);
      alert("News updated successfully!");
      refreshNews();
      cancelEdit();
    } catch (error) {
      console.error("Error updating news:", error);
    }
  };

  if (!news) {
    return <p>No news selected for update.</p>;
  }

  return (
    <div>
      <h2>Edit News</h2>
      <form onSubmit={handleUpdate}>
        <div className="mb-3">
          <label className="form-label">Title:</label>
          <input type="text" className="form-control" value={title} onChange={(e) => setTitle(e.target.value)} required />
        </div>

        <div className="mb-3">
          <label className="form-label">Content:</label>
          <textarea className="form-control" rows="4" value={content} onChange={(e) => setContent(e.target.value)} required />
        </div>

        <div className="mb-3">
          <label className="form-label">Category:</label>
          <input type="text" className="form-control" value={category} onChange={(e) => setCategory(e.target.value)} required />
        </div>

        <div className="mb-3">
          <label className="form-label">Author:</label>
          <input type="text" className="form-control" value={author} onChange={(e) => setAuthor(e.target.value)} required />
        </div>

        <button type="submit" className="btn btn-success w-100">Update News</button>
        <button type="button" className="btn btn-secondary w-100 mt-2" onClick={cancelEdit}>Cancel</button>
      </form>
    </div>
  );
}
