import React, { useState } from "react";
import axios from "axios";

export default function PersonalizedNews() {
  const [selectedCategory, setSelectedCategory] = useState("");
  const [newsList, setNewsList] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  // Predefined categories (as stored in SQL)
  const categories = [
    "SPORTS", "ENTERTAINMENT", "POLITICS", "TECHNOLOGY",
    "HEALTH", "BUSINESS", "SCIENCE", "EDUCATION"
  ];

  const fetchPersonalizedNews = async (e) => {
    e.preventDefault();
    if (!selectedCategory) {
      setError("Please select a category.");
      return;
    }

    setLoading(true);
    setError("");
    setNewsList([]);

    try {
      const formattedCategory = selectedCategory.toUpperCase();

      console.log(`🔍 Fetching news for: ${formattedCategory}`);

      const response = await axios.get("http://localhost:8080/admin/personalizedNews", {
        params: { category: formattedCategory }, // Use 'category' as per backend
      });

      console.log("✅ API Response:", response.data);

      if (response.data.length === 0) {
        setError("No news found for this category.");
      } else {
        setNewsList(response.data);
      }
    } catch (error) {
      console.error("❌ Error fetching news:", error);
      setError("Failed to fetch personalized news. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-6 bg-gray-900 text-white rounded-lg shadow-lg max-w-5xl mx-auto mt-6">
      <h1 className="text-3xl font-bold mb-6 text-center text-blue-400">Personalized News</h1>

      {/* Category Selection Form */}
      <form onSubmit={fetchPersonalizedNews} className="mb-6 flex flex-col md:flex-row items-center gap-4">
        <select
          className="flex-1 px-4 py-3 rounded-md border border-gray-600 bg-gray-800 text-white focus:ring-2 focus:ring-blue-500"
          value={selectedCategory}
          onChange={(e) => setSelectedCategory(e.target.value)}
          required
        >
          <option value="">-- Select a Category --</option>
          {categories.map((category, index) => (
            <option key={index} value={category}>
              {category.charAt(0).toUpperCase() + category.slice(1).toLowerCase()}
            </option>
          ))}
        </select>
        <button
          type="submit"
          disabled={loading}
          className={`px-5 py-3 rounded-md text-white transition duration-300 ${
            loading ? "bg-gray-600 cursor-not-allowed" : "bg-blue-600 hover:bg-blue-700"
          }`}
        >
          {loading ? "Fetching..." : "Fetch News"}
        </button>
      </form>

      {/* Error Message */}
      {error && <p className="text-red-400 text-center text-lg mb-4">{error}</p>}

      {/* News Table */}
      {newsList.length > 0 && !error && (
        <div className="overflow-x-auto">
          <table className="w-full border-collapse border border-gray-700 rounded-lg text-left">
            <thead className="bg-blue-600 text-white">
              <tr>
                <th className="border border-gray-600 px-6 py-3">Title</th>
                <th className="border border-gray-600 px-6 py-3">Category</th>
                <th className="border border-gray-600 px-6 py-3">Author</th>
                <th className="border border-gray-600 px-6 py-3">Date</th>
                <th className="border border-gray-600 px-6 py-3">Content</th>
              </tr>
            </thead>
            <tbody>
              {newsList.map((news, index) => (
                <tr key={index} className={index % 2 === 0 ? "bg-gray-800" : "bg-gray-700"}>
                  <td className="border border-gray-600 px-6 py-3">{news.title}</td>
                  <td className="border border-gray-600 px-6 py-3">{news.category}</td>
                  <td className="border border-gray-600 px-6 py-3">{news.author || "N/A"}</td>
                  <td className="border border-gray-600 px-6 py-3">{news.date || "N/A"}</td>
                  <td className="border border-gray-600 px-6 py-3 truncate w-48">
                    <span className="tooltip" title={news.content}>
                      {news.content.length > 50 ? news.content.slice(0, 50) + "..." : news.content}
                    </span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}
