import React, { useState } from "react";
import axios from "axios";

export default function UpdateNewsById() {
  const [newsId, setNewsId] = useState(""); // Stores the user input for ID
  const [news, setNews] = useState(null); // Stores fetched news data
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [category, setCategory] = useState("");
  const [author, setAuthor] = useState("");
  const [loading, setLoading] = useState(false);
  const url = "http://localhost:8080/admin";

  // Fetch news by ID
  const fetchNews = async () => {
    if (!newsId) {
      alert("Please enter a valid News ID.");
      return;
    }

    setLoading(true);
    try {
      const response = await axios.get(`${url}/getNews/${newsId}`);
      const data = response.data;
      setNews(data);
      setTitle(data.title);
      setContent(data.content);
      setCategory(data.category);
      setAuthor(data.author);
    } catch (error) {
      console.error("Error fetching news:", error);
      alert("News not found with the given ID.");
      setNews(null);
    }
    setLoading(false);
  };

  // Update news details
  const handleUpdate = async (e) => {
    e.preventDefault();
    if (!news) {
      alert("Please fetch the news first.");
      return;
    }

    const updatedNews = { title, content, category, author };

    try {
      await axios.put(`${url}/updateNews/${newsId}`, updatedNews);
      alert("News updated successfully!");
    } catch (error) {
      console.error("Error updating news:", error);
      alert("Failed to update news.");
    }
  };

  return (
    <div>
      <h2>Update News by ID</h2>

      {/* Input for entering News ID */}
      <div className="mb-3">
        <label className="form-label">Enter News ID:</label>
        <input
          type="text"
          className="form-control"
          value={newsId}
          onChange={(e) => setNewsId(e.target.value)}
          required
        />
        <button className="btn btn-primary mt-2" onClick={fetchNews} disabled={loading}>
          {loading ? "Fetching..." : "Fetch News"}
        </button>
      </div>

      {/* Form to edit news details */}
      {news && (
        <form onSubmit={handleUpdate}>
          <div className="mb-3">
            <label className="form-label">Title:</label>
            <input
              type="text"
              className="form-control"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              required
            />
          </div>

          <div className="mb-3">
            <label className="form-label">Content:</label>
            <textarea
              className="form-control"
              rows="4"
              value={content}
              onChange={(e) => setContent(e.target.value)}
              required
            />
          </div>

          <div className="mb-3">
            <label className="form-label">Category:</label>
            <input
              type="text"
              className="form-control"
              value={category}
              onChange={(e) => setCategory(e.target.value)}
              required
            />
          </div>

          <div className="mb-3">
            <label className="form-label">Author:</label>
            <input
              type="text"
              className="form-control"
              value={author}
              onChange={(e) => setAuthor(e.target.value)}
              required
            />
          </div>

          <button type="submit" className="btn btn-success w-100">Update News</button>
        </form>
      )}
    </div>
  );
}
