import React, { useState } from "react";
import axios from "axios";

export default function UpdateNews({ news, refreshNews, cancelEdit }) {
  const [title, setTitle] = useState(news.title);
  const [content, setContent] = useState(news.content);
  const [category, setCategory] = useState(news.category);
  const [author, setAuthor] = useState(news.author);

  const url = "http://localhost:8080/admin";

  const handleUpdate = async (e) => {
    e.preventDefault();
    const updatedNews = { title, content, category, author };

    try {
      await axios.put(`${url}/updateNews/${news.id}`, updatedNews);
      alert("News updated successfully!");
      refreshNews();
      cancelEdit(); // Exit edit mode
    } catch (error) {
      console.error("Error updating news:", error);
    }
  };

  return (
    <div>
      <h2>Edit News</h2>
      <form onSubmit={handleUpdate}>
        <div className="mb-3">
          <label className="form-label">Title:</label>
          <input type="text" className="form-control" required value={title} onChange={(e) => setTitle(e.target.value)} />
        </div>

        <div className="mb-3">
          <label className="form-label">Content:</label>
          <textarea className="form-control" rows="4" required value={content} onChange={(e) => setContent(e.target.value)} />
        </div>

        <div className="mb-3">
          <label className="form-label">Category:</label>
          <input type="text" className="form-control" required value={category} onChange={(e) => setCategory(e.target.value)} />
        </div>

        <div className="mb-3">
          <label className="form-label">Author:</label>
          <input type="text" className="form-control" required value={author} onChange={(e) => setAuthor(e.target.value)} />
        </div>

        <button type="submit" className="btn btn-success w-100">Update News</button>
        <button type="button" className="btn btn-secondary w-100 mt-2" onClick={cancelEdit}>Cancel</button>
      </form>
    </div>
  );
}
