import React, { useState, useEffect } from "react";
import axios from "axios";

export default function AllNews() {
  const [newsList, setNewsList] = useState([]);
  const url = "http://localhost:8080/admin"; // Backend base URL

  // Fetch all news
  const fetchAllNews = async () => {
    try {
      const response = await axios.get(`${url}/allNews`);
      setNewsList(response.data);
    } catch (error) {
      console.error("Error fetching news:", error);
    }
  };

  useEffect(() => {
    fetchAllNews();
  }, []);

  return (
    <div className="p-4 bg-gray-900 text-white rounded-md">
      <h3 className="text-lg font-semibold mb-2">All News</h3>
      <table className="w-full text-left border-collapse border border-gray-700">
        <thead>
          <tr className="bg-gray-800">
            <th className="p-2 border border-gray-700">ID</th>
            <th className="p-2 border border-gray-700">Title</th>
            <th className="p-2 border border-gray-700">Category</th>
            <th className="p-2 border border-gray-700">Author</th>
          </tr>
        </thead>
        <tbody>
          {newsList.length > 0 ? (
            newsList.map((news) => (
              <tr key={news.id} className="hover:bg-gray-800">
                <td className="p-2 border border-gray-700">{news.id}</td>
                <td className="p-2 border border-gray-700">{news.title}</td>
                <td className="p-2 border border-gray-700">{news.category}</td>
                <td className="p-2 border border-gray-700">{news.author}</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="4" className="text-center p-2">No news found.</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}
